'',
'--    CEN_CRUISE.DB_LOG_PKG.ADD_LOG_ENTRY(''DEBUG'', ''PL/SQL returning SQL Query for Gear Shuttle'', ''The value of P230_CRUISE_LEG_ID is: ''||:P230_CRUISE_LEG_ID, V_PROC_RETURN_CODE);',
