insert into ccd_data_sets (DATA_SET_DESC, DATA_SET_TYPE_ID, DATA_SET_DOI, DATA_SET_INPORT_URL, DATA_SET_ACCESS_URL, DATA_SET_ARCHIVE_URL, CRUISE_LEG_ID, DATA_SET_STATUS_ID) values ('This accession contains the Modular Optical Underwater Survey System (MOUSS) stereo video data from SE-17-02 (03/09/2017 - 03/22/2017) collected around the Main Hawaiian Islands (MHI) for the stock assessment of the deep-7 bottomfish species. Each video is fifteen minutes from the MOUSS touchdown on the seafloor as that is the literature based standard for deep-7 species. The products also include depth and temperature data collected using Temperature Depth Recorder (TDR) along with temporal and spatial information.', (SELECT DATA_SET_TYPE_ID FROM CCD_DATA_SET_TYPES WHERE DATA_SET_TYPE_NAME = 'MOUSS Video'), '', 'https://inport.nmfs.noaa.gov/inport/item/51818', '', '', (SELECT CRUISE_LEG_ID FROM CCD_CRUISE_LEGS where leg_name = 'SE-17-02'), (SELECT DATA_SET_STATUS_ID FROM CCD_DATA_SET_STATUS where status_code = 'QC'));
